class Solution {
public:
    const long long MOD = 1e9 + 7;
    vector<vector<vector<long long>>> memo;

    long long solve(int ind, long long mn, long long mx, vector<int>& nums) {
        if (ind >= nums.size()) {
            return 1;      }


        if (memo[ind][mn][mx] != -1) {
            return memo[ind][mn][mx];
        }

        long long ans = 0;
        int a = nums[ind];

        for (long long i = mn; i <= a; i++) {
            long long b = a - i;
            if (b <= mx) {
                ans = (ans + solve(ind + 1, i, b, nums)) % MOD;
            }
        }

        memo[ind][mn][mx] = ans;
        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        memo.resize(n, vector<vector<long long>>(51, vector<long long>(51, -1)));

        long long ct = 0;
        for (int i = 0; i <= nums[0]; i++) {
            ct = (ct + solve(1, i, nums[0] - i, nums)) % MOD;
        }
        return ct;
    }
};
