class Solution {
public:
    int func(int i, vector<int> &arr, int prevNum, vector<vector<int>> &dp) {
        int mod = 1e9+7;
        if(i >= arr.size()) return 1;
        // cout<<i<<endl;

        if(dp[i][prevNum+1] != -1) return dp[i][prevNum+1];
        // cout<<"one"<<endl;

        long long sum = 0;
        if(prevNum == -1) {
            for(int j = 0; j <= arr[i]; j++) {
                sum =  (sum % mod + func(i+1, arr, j, dp)*1ll % mod) % mod;
            }
        } else {
            for(int j = prevNum; j <= arr[i]; j++) {
                int firstArrNum = j, secondArrNum = arr[i]-j;
                if(secondArrNum <= arr[i-1]-prevNum)
                    sum = ( sum % mod + func(i+1, arr, j, dp)*1ll % mod ) % mod;
            }
        }
        // cout<<"one"<<endl;

        return dp[i][prevNum+1] = sum % mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(2100, vector<int>(55, -1));

        return func(0, nums, -1, dp);
    }
};
