#define ll long long int
const ll mod = 1e9+7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        ll dp[n][51][51];

        memset(dp,0,sizeof(dp));

        for(int x=0,y=nums[0];y>=0;x++,y--)
        {
            dp[0][x][y] = 1;
        }

        for(ll i=1;i<n;i++)
        {
            for(int x=0,y=nums[i];y>=0;x++,y--)
            {
                for(int j=x;j>=0;j--)
                {
                    int x1 = j;
                    int y1 = nums[i-1]-x1;

                    if(y1<y)
                        continue;

                    dp[i][x][y] = (dp[i][x][y]%mod+dp[i-1][x1][y1]%mod)%mod;
                }
            }
        }

        ll ans = 0;

        for(int i=0;i<51;i++)
        {
            for(int j=0;j<51;j++)
            {
                ans = (ans%mod+dp[n-1][i][j]%mod)%mod;
            }
        }

        return ans;
    }
};
