class Solution {

    private int MOD = 1000000007;
    private int[][] memo;


    private int countPairs(int[] nums, int i, int minVal) {
        if (i == nums.length) {
            return 1;
        }

        if (memo[i][minVal] != -1) {
            return memo[i][minVal];
        }

        int count = 0;
        for (int x = minVal; x <= nums[i]; x++) {
            int y = nums[i] - x;
            int val = (i > 0) ? nums[i-1] - minVal:1001;

            if (y <= val) {
                count = (count + countPairs(nums, i + 1, x)) % MOD;
            }
        }

        return memo[i][minVal] = count;
    }

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        memo = new int[n][1005];

        for (int[] row : memo) {
            Arrays.fill(row, -1);
        }

        return countPairs(nums, 0, 0);
    }
}
