class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        M = max(nums) + 1
        dp = [0 for _ in range(M)]
        for i in range(nums[0]+1):
            dp[i] = 1

        res = 0
        MOD = (10 ** 9 + 7)

        for i, num in enumerate(nums[1:], start=1):
            new_dp = [0 for _ in range(M)]
            for val in range(num+1):
                for prev_val in range(val+1):
                    if nums[i-1] - prev_val >= nums[i] - val:
                        new_dp[val] += dp[prev_val] % MOD
            dp = new_dp

        return sum(dp) % MOD
