class Solution {
public:

    const int MOD = 1e9 + 7;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(101, 0));

        // Initialize the first row of dp
        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;  // arr1[0] can be j, arr2[0] = nums[0] - j
        }

        // Fill the dp table
        for (int i = 1; i < n; ++i) {
            vector<int> new_dp(101, 0);
            for (int j = 0; j <= nums[i]; ++j) {
                int arr2_val = nums[i] - j;
                for (int k = 0; k <= j; ++k) {
                    int prev_arr2_val = nums[i-1] - k;
                    if (arr2_val <= prev_arr2_val) {
                        new_dp[j] = (new_dp[j] + dp[i-1][k]) % MOD;
                    }
                }
            }
            dp[i] = new_dp;
        }

        // Calculate the final answer
        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
