class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int MOD = 1000000007;
        int[][] dp = new int[n][51];

        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
        }

        for (int i=1; i<n; i++) {
            for (int j=0; j<=nums[i]; j++) {
                for (int k=0; k<=j; k++) {
                    int currArr2 = nums[i] - j;
                    int prevArr2 = nums[i - 1] - k;
                    if (prevArr2 >= currArr2) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
                    }
                }
            }
        }

        int result = 0;
        for (int j=0; j<=50; j++) {
            result = (result + dp[n - 1][j]) % MOD;
        }

        return result;
    }
}
