class Solution:
    def __init__(self):
        self.mod = 10**9 + 7
        self.dp = []
        self.n = 0
        self.v = []

    def rec(self, i, prev):
        if i == self.n:
            return 1  # Base case: Reached the end, so return 1 valid sequence

        if self.dp[i][prev] != -1:
            return self.dp[i][prev]  # Check if result is already computed

        ans = 0

        # Iterate over possible values for arr1[i] from prev to v[i]
        for k in range(prev, self.v[i] + 1):
            if i == 0:
                # For the first element, simply recurse without any comparison
                ans = (ans + self.rec(i + 1, k)) % self.mod
            else:
                # Ensure that the sequence remains monotonic
                if self.v[i - 1] - prev >= self.v[i] - k:
                    ans = (ans + self.rec(i + 1, k)) % self.mod

        self.dp[i][prev] = ans  # Store the computed result
        return ans

    def countOfPairs(self, nums):
        self.n = len(nums)
        self.v = nums[:]

        max_element = max(nums)  # Find the maximum element in nums

        # Initialize dp array with -1 indicating uncomputed results
        self.dp = [[-1 for _ in range(max_element + 10)] for _ in range(self.n)]

        # Start the recursion from the first index with an initial prev value of 0
        return self.rec(0, 0)
