class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int ans = 0;
        int n = nums.size();
        int mod = 1000000007;

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));

        for (int j = 0; j <= nums[0]; j++) {
            int k = nums[0] - j;
            if (k < 0) continue;
            dp[0][j][k] = 1;
        }

        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                int k = nums[i] - j;
                if (k < 0) continue;

                for (int pj = 0; pj <= nums[i-1]; pj++) {
                    int pk = nums[i-1] - pj;
                    if (pk < 0) continue;

                    if (j >= pj && k <= pk) {
                        dp[i][j][k] = (dp[i][j][k] + dp[i-1][pj][pk]) % mod;
                    }
                }
            }
        }

        for (int j = 0; j <= nums[n-1]; ++j) {
            int k = nums[n-1] - j;
            if (k < 0) continue;   
            ans = (ans + dp[n-1][j][k]) % mod;
        }

        return ans;
    }
};
