#define ll long long int
ll Solve(ll index,ll prev,vector<ll> &v,vector<vector<ll>> &dp, ll &MOD){
    if(index==v.size()){
        return 1;
    }
    if(dp[index][prev]!=-1){
        return dp[index][prev];
    }
    dp[index][prev]=0;
    for(ll i=prev;i<=v[index];i++){
        if(index-1!=-1){
            if(v[index]-i<=v[index-1]-prev){
                dp[index][prev]=(dp[index][prev]+Solve(index+1,i,v,dp,MOD))%MOD;
            }
        }
        else{
            dp[index][prev]=(dp[index][prev]+Solve(index+1,i,v,dp,MOD))%MOD;
        }
    }
    return (dp[index][prev])%MOD;
}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<ll>> dp(nums.size(),vector<ll>(51,-1));
        vector<ll> v;
        for(int i=0;i<nums.size();i++){
            v.push_back((ll)(nums[i]));
        }
        ll MOD=1e9+7;
        return Solve(0,0,v,dp,MOD)%MOD;
    }
};
