using ll = long long;

class Solution {
 public:
  int countOfPairs(vector<int>& nums) {
    int n = nums.size();
    vector<ll> dp(51, 0);
    for (int i = 0; i <= nums[0]; ++i) {
      dp[i] = 1;
    }
    for (int t = 1; t < n; ++t) {
      vector<ll> new_dp(51, 0);
      int c = nums[t];
      for (int i = 0; i <= c; ++i) {
        for (int j = 0; j <= 50; ++j) {
          if (i <= j && nums[t] - i >= nums[t - 1] - j) {
            new_dp[i] += dp[j];
          }
        } 
      }
      swap(dp, new_dp);
      for (auto& c : dp) {
        c %= MOD;
      }
    }
    ll ret = 0;
    for (auto c : dp) {
      ret += c;
    }
    ret %= MOD;
    return ret;
  }

  static constexpr ll MOD = 1000000007;
};
