class Solution {
public:
    int n;
    int dp[2020][51][51];
    int mod=1e9+7;

    int helper(vector<int>& nums,int level,int prev,int prev1){
        if(level==n){
            return 1;
        }
        if(dp[level][prev][prev1]!=-1)return dp[level][prev][prev1]%mod;
        int ans=0;
        for(int i=prev;i<=nums[level];i++){
          if(i>=prev and nums[level]-i<=prev1){
          ans=((ans%mod)+(helper(nums,level+1,i,nums[level]-i)%mod))%mod;
          }
        }

        return dp[level][prev][prev1]=ans%mod;

    }
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        memset(dp,-1,sizeof(dp));
        return helper(nums,0,0,nums[0]);
    }
};
