class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, 0));

        for (int j = 0; j <= nums[0]; ++j) dp[0][j] = 1;

        int mod = 1e9+7;

        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                dp[i][j] = 0;

                for (int k = 0; k <= j; ++k) {
                    int arr2_i = nums[i] - j;
                    int arr2_i_prev = nums[i-1] - k;

                    if (arr2_i <= arr2_i_prev) {
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % mod;
                    }
                }
            }
        }

        int ans = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            ans = (ans + dp[n-1][j]) % mod;
        }

        return ans;
    }
};
