class Solution {
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;
    int solve(int i, int p1, int p2, vector<int>& nums, int n) {
        if (i == n) {
            return 1;
        }
        if (dp[i][p1][p2] != -1) {
            return dp[i][p1][p2];
        }
        int tc = 0;
        for (int arr1_i = p1; arr1_i <= nums[i]; arr1_i++) {
            int arr2_i = nums[i] - arr1_i;
            if (arr2_i <= p2) {
                tc += solve(i + 1, arr1_i, arr2_i, nums, n);
                tc %= MOD;
            }
        }
        return dp[i][p1][p2] = tc;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        dp.resize(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(0, 0, nums[0], nums, n);
    }
};
