class Solution {
public:
    int solve(vector<int> &nums, int i, int prev1, int prev2,vector<vector<vector<int>>> &dp){
        if(i==nums.size()) return 1;
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        int ans = 0;
        for(int k=0;k<=nums[i];k++){
            if(k>=prev1 && (nums[i]-k)<=prev2){
                ans = (ans + solve(nums,i+1,k,nums[i]-k,dp)%1000000007)%1000000007;
            }
        }
        return dp[i][prev1][prev2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size(), vector<vector<int>> (100, vector<int> (100,-1)));
        return solve(nums,0,0,99,dp);
    }
};
