class Solution {
public:
    int mod=1e9+7;
    int dp[2002][55];
    int solve(int i, int aval, vector<int>&nums){
        int n=nums.size();
        if(i>=n)return 1;
        if(dp[i][aval+1]!=-1)return dp[i][aval+1];

        int ans=0;
        for(int k=0;k<=nums[i];k++){
            int x=nums[i]-k;
            int aprev=aval;
            int bprev=0;
            if(i!=0)
                bprev=nums[i-1]-aval;
            if(i==0 || (aprev<=k && bprev>=x)){
                ans=(ans+solve(i+1, k, nums))%mod;
            }
        }

        return dp[i][aval+1]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int n=nums.size();
        return solve(0,-1,nums);
    }
};
