class Solution {
    int mod=(int)1e9+7;
    int[][][]dp;
    public int helper(int idx ,int []nums , int prev1 , int prev2){
        if(idx==nums.length) return 1;
        if(dp[idx][prev1][prev2]!=-1) return dp[idx][prev1][prev2];
        int count=0;
        for(int i=0;i<=nums[idx];i++){
            int left=i;
            int right=nums[idx]-left;

            if(left>=prev1 && right<=prev2){
               count=(count + helper(idx+1,nums,left,right))%mod;
            }
        }
        return dp[idx][prev1][prev2]=count;
    }
    public int countOfPairs(int[] nums) {
        dp=new int[nums.length][52][52];
        for(int [][]d : dp) for(int []r : d) Arrays.fill(r,-1);
        return helper(0,nums,0,51);
    }
}
