class Solution {
public:
    long long f(int i,int j,int k,vector<int>&nums,vector<vector<vector<long long>>>&dp){
        if(i>=nums.size()) return 1;
        long long MOD=1e9+7;
        long long ans=0;
        if(dp[i][j][k]!=-1) return dp[i][j][k];
        for(int a=j;a<=nums[i];a++){
            for(int b=k;b>=0;b--){
                if(a+b==nums[i]){
                    ans=ans+f(i+1,a,b,nums,dp);
                    ans=ans%MOD;
                }
            }
        }
        return dp[i][j][k]=ans;
    }
    int countOfPairs(vector<int>& nums) {
      int n=nums.size();
        vector<vector<vector<long long >>> dp(n,vector<vector<long long>> (51,vector<long long> (51,-1)));
        return f(0,0,nums[0],nums,dp);
    }
};
