class Solution {
public:
    vector<vector<vector<int>>> dp;
    int mod = 1e9 + 7;
    int solve(int i, int prev_min , int prev_max , vector<int>& nums){
        if(i == nums.size()){
            return 1;
        }
        if(dp[i][prev_min + 1][prev_max] != -1){
            return dp[i][prev_min+1][prev_max];
        }
        int cnt = 0;
        int first = max(prev_min , 0) , second = min(prev_max , nums[i]);
        while(first <= nums[i] && second >= 0){
            if(first + second == nums[i])
            {   
                cnt = (solve(i + 1, first , second , nums) + cnt) % mod;
                first ++ , second --;
            }
            else{
                if(first + second < nums[i]) first ++;
                else second --;
            }
        }
        return dp[i][prev_min + 1][prev_max] = cnt;
    }
    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size() + 4 , vector<vector<int>> (53 ,vector<int>(53, -1)));
        return solve(0 , -1 , 51 , nums);
    }
};
