class Solution {
    int helper(int arr[], int dp[][], int i, int a, int b) {
        if(a >= 51 || b < 0) return 0;
        if(i == arr.length) return 1;
        if(dp[i][a] != -1) return dp[i][a];
        int ans = 0;
        for(int idx = a; idx <= Math.min(arr[i], 50); idx++) {
            if(arr[i] - idx <= b) ans = (ans + helper(arr, dp, i + 1, idx, arr[i] - idx)) % 1000000007;
        }
        return dp[i][a] = ans;
    }

    public int countOfPairs(int[] nums) {
        int dp[][] = new int[nums.length][51];
        for(int i = 0; i < nums.length; i++) Arrays.fill(dp[i], -1);
        return helper(nums, dp, 0, 0, 50);
    }
}
