class Solution {
public:
    int dp[2001][51];
    int MOD = 1e9 + 7;
    int f(int ind, int prev, vector<int> &nums) {
        if(ind == nums.size()) return 1;

        if(dp[ind][prev] != -1) return dp[ind][prev];

        long long count = 0;
        for(int i=prev; i<=nums[ind]; i++) {
            int prevAr2 = nums[ind-1] - prev;
            int curAr2 = nums[ind] - i;

            if(curAr2 <= prevAr2) {
                count += f(ind+1, i, nums);
                count %= MOD;
            }
        }

        return dp[ind][prev] = count % MOD;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));

        long long ans = 0;
        for(int i=0; i<=nums[0]; i++) {
            ans += f(1, i, nums);
            ans %= MOD;
        }

        return ans;
    }
};
