class Solution {
public:
    int dp[2001][51];
    int mod = 1e9+7;
    int solve(vector<int>& nums, int ind, int pre) {
        if (ind >= nums.size()) return 1;
        if (dp[ind][pre] != -1) return dp[ind][pre];

        int ans = 0;
        for (int j = 0; j <= nums[ind]; j++) {
            if (j >= pre && nums[ind]-j <= nums[ind-1] - pre ) {
                ans = (ans + solve(nums, ind + 1, j)%mod)%mod;
            }
        }
        return dp[ind][pre] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));

        int result = 0;
        for (int i = 0; i <= nums[0]; i++) {
            // if (i <= nums[0] - i) {
                result = (result+solve(nums, 1, i)%mod)%mod;;
            // }
        }

        return result;
    }
};
