class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<long long>> dp(n,vector<long long>(51,-1));
        for(int i=0;i<=nums[0];i++)dp[0][i] = 1;
        const int mod = 1e9 + 7;
        for(int i=1;i<n;i++){
            for(int cur= 0; cur<=nums[i];cur++){
                for(int prev=0; prev<=min(cur,nums[i-1]);prev++){
                    if(dp[i-1][prev] == -1)continue;
                    if(dp[i][cur]==-1)
                        dp[i][cur] = 0;
                    int cur2 = nums[i]-cur;
                    int prev2 = nums[i-1]-prev;

                    if(cur2 <= prev2){
                        (dp[i][cur] += dp[i-1][prev])%=mod;
                    }
                    // cout << cur << " " << prev << " " << cur2 << " " << prev2 << " " << dp[i][cur] << endl;
                }
            }
        }

        long long ans = 0;
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<5;j++)cout << dp[i][j] << " ";
        //     cout << endl;
        // }
        for(int i=0;i<=50;i++){
            if(dp[n-1][i] != -1){
                // cout << dp[n-1][i] << " ";
                ans += dp[n-1][i];
                ans %= mod;
            }
        }
        // cout << endl;
        return ans;

    }
};
