class Solution {
public:
    int dp[2002][52][52];
    int fun(vector<int>&nums, int ind, int m1, int m2){
        if(ind==nums.size())return 1;
        // if(max(m1,m2)>nums[ind])return 0;
        if(dp[ind][m1][m2]!=-1)return dp[ind][m1][m2];
        int f=0,ctr=0;
        if(m2>=m1)f=1;
        for(int i=m1;i<=nums[ind];i++){
            int temp=nums[ind]-i;
            if(temp<=m2){

                    ctr+=(fun(nums,ind+1,i,temp)%1000000007);
                ctr=ctr%1000000007;

            }
        }
        return dp[ind][m1][m2]=ctr%1000000007;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int n=nums.size();
        return fun(nums,0,0,51)%1000000007;
    }
};
