class Solution {
public:
    int solve(vector<int>& nums,int p1,int p2,int i,vector<vector<vector<int>>>& dp){
        int n=nums.size();
        if(i>=n) return 1;
            long long MOD=1e9+7;
        if (dp[i][p1][p2] != -1) return dp[i][p1][p2];
        long long cnt=0;
        for(int ii=p1;ii<=nums[i];ii++){
            int x=nums[i]-ii;
            if(p2>=x){
                cnt= (cnt+ solve(nums,ii,x,i+1,dp)% MOD)%MOD;
            }
        }
        return dp[i][p1][p2]=cnt;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(52, vector<int>(52, -1)));
         long long MOD=1e9+7;
        long long ans=solve(nums,0,51,0,dp)%MOD;
        return ans;
    }
};
