class Solution {
public:
    int mod = 1e9 + 7;
    int helper(int i,int prev1,int prev2, vector<int> &nums, vector<vector<vector<int>>> &dp){
        if(i == nums.size()) return 1;
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        int ans = 0;
        for(int j = prev1; j <= nums[i]; ++j){
            if(nums[i] - j <= prev2) ans = (ans + helper(i+1,j,nums[i]-j,nums,dp))%mod;
        }
        return dp[i][prev1][prev2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        ios_base::sync_with_stdio(false);cin.tie(NULL);cout.tie(NULL);
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int> (51,-1)));
        int ans = helper(0,0,50,nums,dp);
        return ans;
    }
};
