class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vll> dp(n, vll(51, 0));  // 假设 nums[i] 最大值为 200

        rep(j, 0, nums[0] + 1) {
            dp[0][j] = 1;
        }

        ll new_dp[51] = {0};
        rep(i, 1, n) {
            // vvi new_dp(n, vi(51, 0));
            mst(new_dp, 0); 
            rep(j, 0, nums[i] + 1) {
                rep(k, 0, j + 1) {  // 遍历之前所有合法的 k
                    int r = nums[i] - j;  // r 是 arr2[i] 的值
                    if (r >= 0 && r <= nums[i-1] - k) {
                        chsum(new_dp[j], dp[i - 1][k]);
                    }
                }
            }
            rep(j, 0, nums[i] + 1) {
                dp[i][j] = new_dp[j];
            }
            // for (int j=0;j<=nums[i];j++) {
            //     printf("dp[%d][%d]=%d\n",i,j,dp[i][j]);
            // }
        }

        ll result = 0;
        rep(j, 0, nums[n-1] + 1) {
            chsum(result, dp[n - 1][j]);
        }
        // cout<<"================================\n";
        return result;
    }
};
