class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10 ** 9 + 7

        @cache
        def fun(i, prev1, prev2):
            if i >= n:
                return 1

            if prev1 > nums[i]:
                return 0

            count = 0
            for new_prev1 in range(prev1, nums[i] + 1):
                new_prev2 = nums[i] - new_prev1
                if new_prev2 <= prev2:
                    count += fun(i + 1, new_prev1, nums[i] - new_prev1)
                    count = count % MOD
            return count


        return fun(0, 0, nums[0]) % MOD
