from functools import lru_cache
from typing import List

MOD = 10**9 + 7

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        @lru_cache(None)
        def dp(i, last1, last2):
            if i == n:
                return 1  

            total_count = 0
            for arr1_val in range(last1, nums[i] + 1):  # arr1 must be non-decreasing
                arr2_val = nums[i] - arr1_val
                if arr2_val <= last2:  # arr2 must be non-increasing
                    total_count += dp(i + 1, arr1_val, arr2_val)
                    total_count %= MOD

            return total_count

        return dp(0, 0, nums[0])
