class Solution:
    def countOfPairs(self, nums):
        n = nums
        l = len(n)
        M = 10**9 + 7
        m = {}

        def backtrack(i, p, q):
            if i == l:
                return 1
            s = (i, p, q)
            if s in m:
                return m[s]
            c = 0
            for x in range(p, n[i] + 1):
                y = n[i] - x
                if y <= q:
                    c += backtrack(i + 1, x, y)
                    c %= M
            m[s] = c
            return c

        return backtrack(0, 0, max(n))
