class Solution {
public:

    int solve(int i, int j, vector<vector<int>> &dp, vector<int> &nums)
    {
        if(dp[i][j]!=-1) return dp[i][j];
        if(j==0)
        {
            if(i<=nums[j]) return dp[i][j]=1;
            else return dp[i][j]=0;
        }
        int sum = 0;
        for(int x=i;x>=0;x--)
        {
            // consider using x and nums[j-1]-x as a duo, first satisfies x<=i
            // and satisfies nums[j-1]-x >= nums[j]-i

            if(i>nums[j]||nums[j]-i<0) continue;
            if(nums[j-1]-x>=nums[j]-i)
            {
                sum = (sum + solve(x,j-1,dp,nums))%1000000007;
            } 
        }
        return dp[i][j]=sum;

    }

    int countOfPairs(vector<int>& nums) 
    {
        vector<vector<int>> dp(51,vector<int>(nums.size(),-1));   
        for(int i=0;i<=50;i++)
        {
            for(int j=0;j<nums.size();j++)
            {
                dp[i][j]=solve(i,j,dp,nums);
            }
        }
        int ans = 0;
        for(int i=0;i<=50;i++)
        {
            ans=(ans+dp[i][nums.size()-1])%1000000007;
        }
        return ans;
    }
};
