/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function (nums) {
  const MOD = Math.pow(10, 9) + 7;
  const n = nums.length;
  const max = Math.max(...nums);
  const dp = new Array(n)
    .fill()
    .map(() => new Array(max + 1).fill().map(() => new Array(max + 1).fill()));

  let ans = 0;
  for (let i = 0; i <= nums[0]; i++) {
    const j = nums[0] - i;
    const next = helper(i, j, nums, 1, dp, MOD, n);
    ans = (ans + next) % MOD;
  }

  return ans;
};

function helper(oi, oj, nums, idx, dp, MOD, n) {
  if (idx >= n) return 1;

  if (dp[idx][oi][oj] !== undefined) return dp[idx][oi][oj];

  let curr = 0;
  for (let i = oi; i <= nums[idx]; i++) {
    const j = nums[idx] - i;
    if (j > oj) continue;

    const next = helper(i, j, nums, idx + 1, dp, MOD, n);
    curr = (curr + next) % MOD;
  }

  return (dp[idx][oi][oj] = curr);
}
