class Solution {
public:
    vector<vector<int>> dp;
    int MOD = 1e9 + 7;
    int s = 51;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<int>>(n, vector<int>(s, -1));

        int ans = 0;
        for (int arr1 = 0; arr1 <= nums[0]; ++arr1) {
            ans = (ans + dfs(1, arr1, nums)) % MOD;
        }

        return ans;
    }

    int dfs(int i, int prevArr1, vector<int>& nums) {
        if (i == nums.size()) return 1;

        if (dp[i][prevArr1] != -1) return dp[i][prevArr1];

        int result = 0;
        int prevArr2 = nums[i - 1] - prevArr1;

        for (int arr1 = prevArr1; arr1 <= nums[i]; ++arr1) {
            int arr2 = nums[i] - arr1;
            if (arr2 <= prevArr2) {
                result = (result + dfs(i + 1, arr1, nums)) % MOD;
            }
        }

        return dp[i][prevArr1] = result;
    }
};
