class Solution {
public:
    int dp[2002][52][52];
    int mod = 1e9+7;
    long long solve(int i, int p1 , int p2, vector<int>&nums){
        if(i>=nums.size()) return 1;
        if(dp[i][p1][p2]!=-1) return dp[i][p1][p2];
        long long ans = 0;
        for(int j = p1; j<=nums[i]; j++){
            if(nums[i]-j<=p2){
                ans = (ans + solve(i+1, j, nums[i]-j, nums))%mod;
            }
        }
        return dp[i][p1][p2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(0, 0, 51, nums);
    }
};
