#include <vector>

const int MOD = 1'000'000'007;

class Solution {
public:
    vector<vector<vector<int>>> dp;

    int dfs(const vector<int>& nums, int i, int p1, int p2) {
        if (i >= nums.size()) {
            return 1;
        }

        if (dp[i][p1][p2] != -1) {
            return dp[i][p1][p2];
        }

        int res = 0;
        for (int j = 0; j <= nums[i]; ++j) {
            int a1 = j;
            int a2 = nums[i] - j;
            if (isValid(a1, p1, a2, p2)) {
                res = (res + dfs(nums, i + 1, a1, a2)) % MOD;
            }
        }

        dp[i][p1][p2] = res;
        return res;
    }

    bool isValid(int a1, int p1, int a2, int p2) {
        return a1 >= p1 && a2 <= p2;
    }

    void initializeDP(int n) {
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
    }

    int countOfPairs(const vector<int>& nums) {
        int n = nums.size();
        initializeDP(n);
        return dfs(nums, 0, 0, 50);
    }
};
