class Solution {
public:
    int sol(vector<int>&nums, int idx, int val1, int val2, vector<vector<vector<int>>>&dp){
        if(idx >= nums.size()) return 1;
        int res = 0;
        int MOD = 1e9+7;
        if(dp[idx][val1][val2] != -1) return dp[idx][val1][val2];
        for(int i=val1;i<=nums[idx];i++){
            for(int j=val2;j>=0;j--){
                if(i+j == nums[idx]){
                    res += sol(nums, idx+1, i, j, dp);
                    res = res%MOD;
                }
            }
        }
        return dp[idx][val1][val2] = res;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51,-1)));
        return sol(nums, 0, 0, nums[0], dp);
    }
};
