#define f first
#define s second
#define pb push_back
#define all(x) x.begin(),x.end()
#define sz(x) (long long)(x).size()
#define pii pair<long long,long long>

class Solution {
private:
    const int mod = 1e9 + 7;
public:
    long long DP(long long fir, long long idx, vector<int> &nums, vector<map<long long, long long>> &dp) {
        long long n = sz(nums);
        if (idx >= n) {
            return 1;
        }
        if (dp[idx].find(fir) != dp[idx].end()) {
            return dp[idx][fir];
        }
        long long ans = 0, sec = INT_MAX;
        if (idx > 0) {
            sec = nums[idx - 1] - fir;
        }
        for (long long i = 0; i <= nums[idx]; i++) {
            if (fir <= i && sec >= (nums[idx] - i)) {
                ans = (ans + DP(i, idx + 1, nums, dp)) % mod;
            }
        }
        return dp[idx][fir] = ans;
    }

    int countOfPairs(vector<int>nums) {
        vector<map<long long, long long>> dp(2000);
        int ans = DP(-INT_MAX, 0, nums, dp);
        return ans % mod;
    }
};
