#define mod 1000000007
class Solution {
public:
    int helper(int i, vector<int>&nums ,int first, int second, vector<vector<vector<int>>> &dp){        
        if(i >= nums.size()){
            return 1;
        }

        if(dp[i][first][second] != -1){
            return dp[i][first][second];
        }
        int p = 0, np = 0;
        for(int j=0;j<=nums[i];j++){
            if(j>= first && (nums[i] - j <= second)){
                p = (p%mod + helper(i+1, nums, j, nums[i] - j, dp)%mod)%mod;
            }
        }
        return dp[i][first][second] = p%mod;        
    }
    int countOfPairs(vector<int>& nums) {
        int first = 0;
        int second = 60;
        int n = nums.size();
        vector<vector<vector<int>>>dp(n + 2, vector<vector<int>>(61, vector<int>(61, -1)));
        return helper(0, nums, first, second, dp);
    }
};
