class Solution {
public:
    const int MOD = 1e9 + 7;

    int solve(int index, int prev1, int prev2, vector<int>& nums, vector<vector<vector<int>>>& dp, int maxVal) {
        if (index == nums.size()) {
            return 1;
        }

        if (dp[index][prev1][prev2] != -1) {
            return dp[index][prev1][prev2];
        }

        int cnt = 0;
        for (int a = prev1; a <= nums[index]; ++a) {
            int b = nums[index] - a;
            if (b <= prev2) {
                cnt = (cnt + solve(index + 1, a, b, nums, dp, maxVal)) % MOD;
            }
        }
        return  dp[index][prev1][prev2] = cnt%MOD ;
    }

    int countOfPairs(vector<int>& nums) {
        int maxVal = *max_element(nums.begin(), nums.end());
        vector<vector<vector<int>>> dp(nums.size(), vector<vector<int>>(100 , vector<int>(100 , -1)));
        return solve(0, 0, 55 , nums, dp, maxVal)%MOD ;
    }
};
