class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int> (51));

        for (int i = 0; i <= nums[0]; ++i) {
            dp[0][i] = 1;
        }

        for (int i = 1; i < n; ++i) {
            for (int x = 0; x <= nums[i]; ++x) {
                int sum = 0;
                for (int j = 0; j <= x; ++j) {
                    if (nums[i - 1] - j >= nums[i] - x)
                        sum = mod_add(sum, dp[i - 1][j], mod);
                }
                dp[i][x] = sum;
            }
        } 

        int total = 0;
        for (int i = 0; i <= nums[n - 1]; ++i) {
            total = mod_add(total, dp[n - 1][i], mod);
        }
        return total;
    }
private:
    const int mod = 1e9 + 7;
    long long mod_add(long long a, long long b, long long m) {
        return (a % m + b % m + m) % m;
    }
};

