class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = pow(10, 9) + 7;
        vector<vector<long long>> dp(nums.size(), vector<long long>(55, 0));
        for (int i = 0 ; i <= nums[0]; i++) {
            dp[0][i] = 1;
        }

        for (int i = 1; i < nums.size(); i++) {
            for (int j = 0; j <= nums[i]; j++) {
                int dec = nums[i - 1] - j;
                for (int k = max(j, nums[i] - dec); k <= nums[i]; k++) {
                    dp[i][k] += dp[i - 1][j];
                    dp[i][k] = dp[i][k] % mod;
                }
            }
        }

        long long res = 0;
        for (int i = 0; i < 55; i++) {
            res += dp[nums.size() - 1][i];
            res = res % mod;
        }
        return res;
    }
};
