class Solution {
public:
    #define ll long long 
    ll mod = 1e9 + 7 ;

    ll func(int indx , int currFirst , vector<int>&nums , vector<vector<int>>&dp){
       ll currSecond = nums[indx] - currFirst ;
       if(currSecond < 0) return 0 ;
       if(indx >= nums.size() - 1) return 1 ;
       if(dp[indx][currFirst] != -1) return dp[indx][currFirst]; 

       ll ans = 0 ;

       for(int i = max(0 , currFirst) ; i <= nums[indx + 1] ; ++i){
            int nextSecond = nums[indx + 1] - i ;
            if(nextSecond <= currSecond){
                 ans += func(indx + 1 , i , nums , dp);
                 ans %= mod ;
            } 
       }

       return dp[indx][currFirst] = ans ; 
    }



    int countOfPairs(vector<int>& nums) {
        int n = nums.size() ;
        vector<vector<int>> dp(n + 1 , vector<int>(55 , -1));

        ll ans = 0 ;

        for(int i = 0 ; i <= nums[0] ; ++i){
            ans += func(0 , i , nums , dp);
            ans %= mod ;
        }

        return ans ;
    }
};
