class Solution {
public:
    #define ll long long int
    ll MOD=1e9+7;
    ll dp[2001][52][52];
    ll mod_mul(ll a, ll b, ll m) {a = a % m; b = b % m; return (((a * b) % m) + m) % m;}
ll mod_add(ll a, ll b, ll m) {a = a % m; b = b % m; return (((a + b) % m) + m) % m;}
    ll help(int i,int lst1,int lst2,vector<int>&vec){
        ll ans=0;
        if(i==vec.size())return 1;
        if(dp[i][lst1+1][lst2+1]!=-1)return dp[i][lst1+1][lst2+1];
        if(lst1==-1){
            for(int j=0;j<=50;j++){
                if(vec[i]-j>=0)
                ans=mod_add(ans,help(i+1,j,vec[i]-j,vec),MOD);
            }
        }
        else{
            for(int j=lst1;j<=50;j++){
                if(vec[i]-j>=0 && vec[i]-j<=lst2){
                    // cout << j << endl;
                    ans=mod_add(ans,help(i+1,j,vec[i]-j,vec),MOD);
                }
            }
        }
        return dp[i][lst1+1][lst2+1]=ans;
    }
    int countOfPairs(vector<int>& vec) {
        int n=vec.size();

        memset(dp,-1,sizeof(dp));
        ll ans=help(0,-1,-1,vec);
        return ans;
    }
};
