class Solution {
    int mod = 1000000007;
    Long[][][] dp;
    public int countOfPairs(int[] nums) {
        dp = new Long[nums.length][52][52];
        return (int)(solve(nums, 0, 0, 51)%mod);
    }

    public long solve(int[] nums, int inx, int prev, int prevD){

        if(inx >= nums.length) return 1;
        if(nums[inx] < prev) return 0;
        if(dp[inx][prev][prevD] != null) return dp[inx][prev][prevD];

        long res = 0;

        for(int i = prev; i<= nums[inx]; i++){
            int curD = nums[inx] - i;
            if(curD <= prevD){
                res += solve(nums, inx+1, i, curD);       
                res = res % mod;
            }
        }


        return dp[inx][prev][prevD] = res;
    }
}
