from functools import lru_cache
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10 ** 9 + 7

        @lru_cache(None)
        def count(i, prev_x, prev_y):
            if i == n:
                return 1

            total_count = 0
            for x in range(prev_x, nums[i] + 1):
                arr2_i = nums[i] - x
                if arr2_i < 0:
                    continue

                if arr2_i > prev_y:
                    continue

                total_count += count(i + 1, x, arr2_i)
                total_count %= MOD

            return total_count

        return count(0, 0, math.inf)
