const int MOD = 1e9 + 7;
vector<vector<vector<int>>> dp;

class Solution {
    int f(int idx, int prev1, int prev2, vector<int>& nums) {
        if (idx == nums.size()) return 1;
        if (dp[idx][prev1][prev2] != -1) return dp[idx][prev1][prev2];

        int count = 0;
        for (int arr1 = prev1; arr1 <= nums[idx]; ++arr1) {
            int arr2 = nums[idx] - arr1;
            if (arr2 >= 0 && arr2 <= prev2) {
                count = (count + f(idx + 1, arr1, arr2, nums)) % MOD;
            }
        }
        return dp[idx][prev1][prev2] = count;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.assign(n, vector<vector<int>>(51, vector<int>(51, -1))); 
        return f(0, 0, 50, nums); 
    }
};
