class Solution {
public:
    vector<vector<vector<long long>>> memo;
    const int MOD = 1e9 + 7;

    long long helper(int ind, int prevl, int prevr, vector<int>& nums, int n) {
        if (ind == n) return 1;

        if (memo[ind][prevl][prevr] != -1) return memo[ind][prevl][prevr];

        long long count = 0;
        for (int i = 0; i <= nums[ind]; ++i) {
            if (prevl <= i && nums[ind] - i <= prevr) {
                count = (count + helper(ind + 1, i, nums[ind] - i, nums, n)) % MOD;
            }
        }

        return memo[ind][prevl][prevr] = count;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memo = vector<vector<vector<long long>>>(n, vector<vector<long long>>(51, vector<long long>(51, -1)));

        return helper(0, 0, 50, nums, n);
    }
};
