class Solution {
private:
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int backtrack(int i, int prev_a, int prev_b, vector<int>& nums) {

        if (i == nums.size()) {
            return 1;
        }

        if (dp[i][prev_a][prev_b] != -1) {
            return dp[i][prev_a][prev_b];
        }

        int count = 0;

        for (int a = prev_a; a <= nums[i]; ++a) {
            int b = nums[i] - a;
            if (b <= prev_b) { 
                count = (count + backtrack(i + 1, a, b, nums)) % MOD;
            }
        }

        return dp[i][prev_a][prev_b] = count;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return backtrack(0, 0, 50, nums);
    }
};
