#include <bits/stdc++.h>
using namespace std;

#define ll long long int
#define INF 1000000000000000 //1e17
const ll mod = 1000000007;
#define N 51 

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n =nums.size() ;

        ll dp[n][N] ;
        memset(dp,0,sizeof(dp)) ;

        //B 
        for(ll i =0 ; i<=nums[0] ;i++ ){
            dp[0][i] =1 ;
        }
        //T
        for(ll i =1 ; i<n ;i++ ){
            for(ll j =0 ; j<N ;j++ ){
                ll k = nums[i]- j ;
                if(k<0)break ;

                for(ll prev = 0 ; prev <=j ;prev++ ){
                    ll pprev = nums[i-1] -prev ;
                    if( pprev >= k){
                        dp[i][j]+=dp[i-1][prev] ;
                        dp[i][j]%=mod ;
                    }
                }
            }
        }

        ll res =0 ;
        for(ll j=0 ; j<N ;j++ ){
            res+=dp[n-1][j] ;
            res%=mod ;
        }
        return res ; 
    }
};
