class Solution {
public:
    int MOD = 1000000007;
    int findPairs(const vector<int>& nums, int temp, int prev1, int prev2, vector<vector<vector<int>>>& dp) {
        if (temp>=nums.size()) {
            return 1;
        }

        if (dp[temp][prev1][prev2]!=-2) {
            return dp[temp][prev1][prev2];
        }

        int ans = 0;
        for (int i = 0; i <= nums[temp]; ++i) {
            int arr1 = i;
            int arr2 = nums[temp] - i;
            if (arr1>=prev1 && arr2<=prev2) {
                ans =(ans+findPairs(nums,temp+ 1, arr1, arr2,  dp))%MOD;
            }
        }

        dp[temp][prev1][prev2] = ans;
        return ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>>dp= vector<vector<vector<int>>>(n,vector<vector<int>>(51,vector<int>(51,-2))); 
        return findPairs(nums, 0, 0, 50, dp);
    }
};
